import React, {Component} from "react";
import Main from '../template/Main'
import axios from 'axios'

const headerProps = {
    icon: 'users',
    title: 'Utilizadores',
    subtitle: 'Registo de utilizadores: Incluir, Listar, Alterar, Excluir!'
}

const baseUrl = 'http://localhost:3001/users'

const initialState = {
    user: { name: '', email: ''},
    list: []
}

export default class UserCrud extends Component {

    state = { ...initialState }

    componentWillMount() {
        axios(baseUrl).then(resp => {
            this.setState({list: resp.data})
        })
    }

    //Esta função faz faz clear ao fazer set do state para o initialState
    clear() {
        this.setState({ user: initialState.user })
    }

    save() {
        // obtém o estado atual do usuário
        const user = this.state.user
        //Verifica se exste um user e se houver usa o verbo Put para atualizar, se não, 
        //usa o Post para criar um novo
        const method = user.id ? 'put' : 'post'
        //Verifica se existe um user e se existir, cria o url com id do user, caso contrário usa o baseUrl
        const url = user.id ? `${baseUrl}/${user.id}/` : baseUrl
        console.log('url:', url);
        axios[method](url, user)
            .then(resp => {
                const list = this.getUpdatedList(resp.data)
                this.setState({ user: initialState.user, list })
            })
    }

    getUpdatedList(user) {
        const list = this.state.list.filter(u => u.id !== user.id)
        if(user) list.unshift(user)
        return list
    }

    updateField(event) {
        const user = { ...this.state.user }
        user[event.target.name] = event.target.value
        this.setState({ user })
    }

    renderForm() {
        return (
            <div className="form">
                <div className="row">
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label>Nome</label>
                            <input type="text" className="form-control"
                            name="name"
                            value={ this.state.user.name }
                            onChange={e => this.updateField(e)}
                            placeholder="Digite o nome..."
                            />
                        </div>
                    </div>
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label>E-mail</label>
                            <input type="text" className="form-control"
                            name="email"
                            value={ this.state.user.email }
                            onChange={e => this.updateField(e)}
                            placeholder="Digite o e-mail..."
                            />
                        </div>
                    </div>
                </div>
                <hr/>
                <div className="row">
                    <div className="col-12 d-flex justify-content-end">
                        <button className="btn btn-primary"
                        onClick={e => this.save(e)}>
                            Salvar
                        </button>
                        <button className="btn btn-primary ml-2"
                        onClick={e => this.clear(e)}>
                            Cancel
                        </button>
                    </div>
                </div>
            </div>
        )
    }

    load(user) {
        this.setState( {user} );
    }

    remove(user) {
        axios.delete(`${baseUrl}/${user.id}`).then(resp => {
            const list = this.getUpdatedList(null)
            this.setState({ list })
        })
    }

    renderTable() {
        return (
            <table className="table mt-4">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>E-mail</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {this.renderRows()}
                </tbody>
            </table>            
        )

    }

    renderRows() {
       return this.state.list.map(user => {
        return (
            <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                        <button className="tbn btn-warning"
                        onClick={ () => this.load(user)}></button>
                        <i className="fa fa-pencil"></i>
                        <button className="btn btn-danger ml-2"
                        onClick={ () => this.remove(user)}></button>
                        <i className="fa fa-trash"></i>
                </td>
            </tr>
        )
       })
    }

    render() {
        console.log(this.state.list)
        return (
            
            <Main {...headerProps} >
               {this.renderForm()}
               {this.renderTable()}
            </Main>
        )
    }
}
